<?php # @link actiontheme.com# @link codex.wordpress.org/Child_Themes# Most customizations can be made via action/filter hooks. # Use this file to add or remove actions/filters. It is also # possible to override parent theme files by copying them into # this folder and editing them here. For example, if you put a# file here called branding.php, it will override the parent # theme's branding.php file.# @link codex.wordpress.org/Plugin_API#Actions# @link codex.wordpress.org/Plugin_API#Filters# @link codex.wordpress.org/Plugin_API/Action_Reference# Apply customizations inside a "theme setup" function:is_admin() or add_action('after_setup_theme', function() {        # Key Functions    # @link codex.wordpress.org/Function_Reference/add_action    # @link codex.wordpress.org/Function_Reference/add_filter    # @link codex.wordpress.org/Function_Reference/wp_enqueue_style    # @link codex.wordpress.org/Function_Reference/wp_enqueue_script        # Example Action    #     add_action('init', function() {            # scripts        $uri = 'http://airve.github.com/js/modernizr/modernizr_shiv.min.js';        wp_enqueue_script('modernizr', $uri, array(), null, false);            }, 1);});#end (omit PHP end tag to prevent "white screen" errors)